mapper.select:
SELECT date_format(create_time,'%Y-%m-%d') FROM wk_crm_activity as a where a.status = 1


    and TO_DAYS(now()) - TO_DAYS(a.create_time) < #{intervalDay}


    and a.content like concat('%', #{search}, '%')


    and a.type != 1


    and date_format(a.create_time,'%Y-%m-%d') between #{startDate} and #{endDate}

GROUP BY date_format(create_time,'%Y-%m-%d')  ORDER BY create_time DESC LIMIT #{page},2

select b.user_id,b.realname,b.img,count(1) as count from wk_crm_activity as a inner join wk_admin_user as b on a.create_user_id = b.user_id where a.type = 4 and b.user_id in
#{userId}
and a.create_time between #{startTime} and #{endTime} group by create_user_id

select b.user_id,b.realname,b.img as userImg,a.activity_id,a.create_time,a.address,a.content,a.activity_type,a.status,a.batch_id,c.customer_id,c.customer_name from wk_crm_activity as a left join wk_admin_user as b on a.create_user_id = b.user_id left join wk_crm_customer as c on a.activity_type_id = c.customer_id where a.type = 4 and a.create_user_id = #{userId}
and a.create_time between #{startTime} and #{endTime}
order by create_time desc
