mapper.select:
select a.employee_appraisal_id, a.employee_id, a.score, a.status, a.level_id, d.level_name, a.read_status, b.employee_name, b.mobile, b.job_number, b.status as employeeStatus, b.is_del, c.name as deptName, e.employee_name as follow_up_employee_name from `wk_hrm_achievement_employee_appraisal` a left join `wk_hrm_employee` b on a.employee_id = b.employee_id left join `wk_hrm_dept` c on b.dept_id = c.dept_id left join wk_hrm_achievement_appraisal_score_level d on a.level_id = d.level_id left join `wk_hrm_employee` e on a.follow_up_employee_id = e.employee_id where a.appraisal_id = #{data.appraisalId}
and b.employee_name like concat('%',#{data.employeeName},'%')
and c.dept_id = #{data.deptId}
and a.level_id = #{data.levelId}
and a.status = #{data.status}

select a.employee_id,a.employee_name,a.mobile,b.name as deptName,a.post,a.status as employeeStatus,date_format(a.entry_time,'%Y-%m-%d') as become_time,a.is_del
from wk_hrm_employee a left join wk_hrm_dept b on a.dept_id = b.dept_id where 1 = 1 and a.entry_status in (1,3)

    and a.employee_name like concat('%',#{data.employeeName},'%')


    and a.mobile like concat('%',#{data.mobile},'%')


    and a.status = #{data.employeeStatus}


    and a.dept_id = #{data.deptId}



        and a.employee_id in

            #{employeeId}



        and 1=2


order by a.is_del
