mapper.select:
select a.employee_evaluato_id, a.employee_appraisal_id, d.appraisal_id, b.employee_id, b.employee_name, b.mobile, c.name as deptName, d.appraisal_name, a.status from `wk_hrm_achievement_employee_evaluato` a left join `wk_hrm_achievement_employee_appraisal` e on a.employee_appraisal_id = e.employee_appraisal_id left join `wk_hrm_employee` b on e.employee_id = b.employee_id left join `wk_hrm_dept` `c` on b.dept_id = `c`.dept_id left join `wk_hrm_achievement_appraisal` d on d.appraisal_id = a.appraisal_id where a.employee_id = #{employeeId}
and a.status = #{data.status}
and a.appraisal_id = #{data.appraisalId}
and (b.employee_name like concat('%',#{data.search},'%') or b.mobile like concat('%',#{data.search},'%') or d.appraisal_name like concat('%',#{data.search},'%'))
order by a.create_time desc

select a.employee_appraisal_id,d.appraisal_id,b.employee_id,b.employee_name,b.mobile, c.name as deptName,d.appraisal_name,a.status from `wk_hrm_achievement_employee_appraisal` a left join `wk_hrm_employee` b on a.employee_id = b.employee_id left join `wk_hrm_dept` `c` on b.dept_id = `c`.dept_id left join `wk_hrm_achievement_appraisal` d on d.appraisal_id = a.appraisal_id where a.follow_up_employee_id = #{employeeId} and a.status = #{status}
and (b.employee_name like concat('%',#{search},'%') or b.mobile like concat('%',#{search},'%') or d.appraisal_name like concat('%',#{search},'%'))
and a.appraisal_id = #{appraisalId}
order by a.create_time desc

select a.employee_evaluato_id, a.employee_appraisal_id, d.appraisal_id, b.employee_id, b.employee_name, b.mobile, c.name as deptName, d.appraisal_name, a.status from `wk_hrm_achievement_employee_evaluato` a left join `wk_hrm_achievement_employee_appraisal` e on a.employee_appraisal_id = e.employee_appraisal_id left join `wk_hrm_employee` b on e.employee_id = b.employee_id left join `wk_hrm_dept` `c` on b.dept_id = `c`.dept_id left join `wk_hrm_achievement_appraisal` d on d.appraisal_id = a.appraisal_id where a.employee_id = #{employeeId}
and a.status = #{status}
order by a.create_time desc
