mapper.select:
select a.*,c.employee_name as leadEmployeeName,d.employee_name as mainEmployeeName,e.name as pName, count(b.is_del = 0 and b.entry_status in (1,3) or null) as allNum, count(b.employment_forms = 1 and b.entry_status in (1,3) and b.is_del = 0 or null) as fullTimeNum, count(b.employment_forms = 2 and b.entry_status in (1,3) and b.is_del = 0 or null) as nuFullTimeNum from `wk_hrm_dept` a left join `wk_hrm_employee` b on a.dept_id = b.dept_id left join `wk_hrm_employee` c on a.leader_employee_id = c.employee_id and c.is_del = 0 left join `wk_hrm_employee` d on a.main_employee_id = d.employee_id and c.is_del = 0 left join wk_hrm_dept e on a.pid = e.dept_id where a.dept_id = #{deptId}
and b.employee_id in
#{employeeId}

select a.employee_id,a.employee_name,a.employment_forms,a.entry_time,a.job_number,a.post,b.name as deptName from wk_hrm_employee a left join wk_hrm_dept b on a.dept_id = b.dept_id where a.dept_id = #{data.deptId}
and a.employee_name like concat('%',#{data.search},'%')
and a.entry_status in (1,3) and a.is_del = 0
and a.employee_id in
#{employeeId}

select dept_id, count(is_del = 0 and entry_status in (1,3) or null) as allNum, count(is_del = 0 and entry_status in (1,3) or null) as myAllNum, count(employment_forms = 1 and entry_status in (1,3) and is_del = 0 or null) as fullTimeNum, count(employment_forms = 1 and entry_status in (1,3) and is_del = 0 or null) as myFullTimeNum, count(employment_forms = 2 and entry_status in (1,3) and is_del = 0 or null) as nuFullTimeNum, count(employment_forms = 2 and entry_status in (1,3) and is_del = 0 or null) as myNuFullTimeNum from wk_hrm_employee where employee_id in
#{employeeId}
group by dept_id
