mapper.select:
select a.i_record_id,a.month,a.year,a.title, count(b.status=1 and (a.status = 1 or (a.status = 0 and c.is_del = 0)) OR NULL) as num, count(b.status=0 and (a.status = 1 or (a.status = 0 and c.is_del = 0)) OR NULL) as stopNum,a.status, sum(if(b.status = 1 and (a.status = 1 or (a.status = 0 and c.is_del = 0)),b.personal_insurance_amount,0)) as personal_insurance_amount, sum(if(b.status = 1 and (a.status = 1 or (a.status = 0 and c.is_del = 0)),b.personal_provident_fund_amount,0)) as personal_provident_fund_amount, sum(if(b.status = 1 and (a.status = 1 or (a.status = 0 and c.is_del = 0)),b.corporate_insurance_amount,0)) as corporate_insurance_amount, sum(if(b.status = 1 and (a.status = 1 or (a.status = 0 and c.is_del = 0)),b.corporate_provident_fund_amount,0)) as corporate_provident_fund_amount from wk_hrm_insurance_month_record a left join wk_hrm_insurance_month_emp_record b on a.i_record_id = b.i_record_id left join wk_hrm_employee c on b.employee_id = c.employee_id where 1=1
and a.year = #{data.year}
and c.employee_id in
#{employeeId}
and 1=2
group by a.i_record_id order by a.create_time desc

select a.i_record_id,a.month,a.year,a.title,count(b.status=1 OR NULL) as num,count(b.status=0 OR NULL) as stopNum,a.status, sum(if(b.status = 1,b.personal_insurance_amount,0)) as personal_insurance_amount, sum(if(b.status = 1,b.personal_provident_fund_amount,0)) as personal_provident_fund_amount, sum(if(b.status = 1,b.corporate_insurance_amount,0)) as corporate_insurance_amount, sum(if(b.status = 1,b.corporate_provident_fund_amount,0)) as corporate_provident_fund_amount from wk_hrm_insurance_month_record a left join wk_hrm_insurance_month_emp_record b on a.i_record_id = b.i_record_id left join wk_hrm_employee c on b.employee_id = c.employee_id where a.i_record_id = #{iRecordId} and (a.status = 1 or (a.status = 0 and c.is_del = 0))
and c.employee_id in
#{employeeId}
