mapper.select:
select b.id,b.salary_num,b.pay_num,b.`year`,b.`month`,b.create_time,b.create_user_id, (select count(*) from wk_hrm_salary_slip a where b.id = a.record_id and a.read_status = 1) as readNum from wk_hrm_salary_slip_record b where 1=1
and b.year = #{data.year} and b.month = #{data.month}
order by b.create_time desc

select id,a.employee_id,b.employee_name,b.job_number,b.post,b.mobile,c.name as deptName,a.read_status,a.remarks from wk_hrm_salary_slip as a
left join wk_hrm_employee as b on a.employee_id = b.employee_id
left join wk_hrm_dept as c on b.dept_id = c.dept_id
where a.record_id = #{data.id}

    and (b.employee_name like concat('%',#{data.search},'%') or b.job_number like concat('%',#{data.search},'%'))


    and a.read_status = #{data.readStatus}


    and a.remarks like concat('%',#{data.remarks},'%')


    and b.dept_id = #{data.deptId}

mapper.sql:
where a.s_record_id = #{sRecordId}

    and b.employee_name like concat('%',#{data.employeeName},'%')


    and b.dept_id = #{data.deptId}



        and d.employee_id is not null


        and d.employee_id is null


group by a.employee_id
