mapper.select:
select a.*, b.dept_id, b.realname, b.img as userImg, soal.customer_ids, soal.contacts_ids, soal.business_ids, soal.contract_ids, (CASE WHEN c.log_id is null THEN 0 ELSE 1 END) as getBulletin, c.customer_num as customerCount, c.business_num as businessCount, c.contract_num as contractCount, c.receivables_money as receivablesMoney, c.activity_num as recordCount, (SELECT COUNT(*) FROM wk_work_task_comment WHERE type_id=a.log_id and type='2') as replyNum FROM wk_oa_log as a LEFT JOIN wk_admin_user as b on a.create_user_id=b.user_id LEFT JOIN wk_oa_log_record as c on a.log_id=c.log_id LEFT JOIN wk_oa_log_relation as soal on soal.log_id=a.log_id WHERE 1 = 1
and a.category_id = #{data.categoryId}
and a.create_user_id in
#{id}
and FIND_IN_SET(#{data.send_user_ids},a.send_user_ids)
and a.create_user_id in
#{id}
and (
a.create_user_id in
#{id}
or
FIND_IN_SET(#{data.send_user_ids},a.send_user_ids)
and a.create_user_id in
#{id}
)
and date_format(a.create_time,#{data.sqlDateFormat}) between #{data.beginTime} and #{data.finalTime}
and date_format(a.create_time,'%Y-%m-%d') between #{data.startTime} and #{data.endTime}
and category_id = #{data.categoryId}
and a.log_id = #{data.logId}
and ( a.content like CONCAT('%',#{data.search},'%') or a.tomorrow like CONCAT('%',#{data.search},'%') or a.question like CONCAT('%',#{data.search},'%') )
and to_days(now()) - to_days(a.create_time) < #{data.recentDays}
order by a.log_id desc

select count(distinct create_user_id) from wk_oa_log
where create_time between #{start} and #{end} and create_user_id in

    #{id}

and category_id = #{type}

SELECT a.*, b.dept_id, b.realname, b.img as userImg, soal.customer_ids, soal.contacts_ids, soal.business_ids, soal.contract_ids, (CASE WHEN c.log_id is null THEN 0 ELSE 1 END) as getBulletin, c.customer_num as customerCount, c.business_num as businessCount, c.contract_num as contractCount, c.receivables_money as receivablesMoney, c.activity_num as recordCount, (SELECT COUNT(*) FROM wk_work_task_comment WHERE type_id=a.log_id and type='2') as replyNum FROM wk_oa_log as a LEFT JOIN wk_admin_user as b on a.create_user_id=b.user_id LEFT JOIN wk_oa_log_record as c on a.log_id=c.log_id LEFT JOIN wk_oa_log_relation as soal on soal.log_id=a.log_id WHERE a.create_time between #{data.start} and #{data.end} and a.create_user_id in
#{id}
and a.category_id = #{data.type}
and b.realname like CONCAT('%',#{data.search},'%')
and (
a.create_user_id in
#{id}
or
FIND_IN_SET(#{data.send_user_ids},a.send_user_ids)
and a.create_user_id in
#{id}
)
order by a.create_time

select user_id,realname,img from wk_admin_user
where user_id not in

    #{id}

and user_id in

    #{id}


    and realname like CONCAT('%',#{data.search},'%')

select
(SELECT COUNT(*) FROM wk_crm_business as c WHERE TO_DAYS(create_time)=TO_DAYS(NOW()) AND owner_user_id in

    #{id}
) as business_num,
COUNT(*) as contract_num,
(SELECT COUNT(*) FROM wk_crm_customer as c WHERE TO_DAYS(create_time)=TO_DAYS(NOW()) and status != 3 AND owner_user_id in

    #{id}
) as customer_num,
(SELECT IFNULL(SUM(money),0) FROM wk_crm_receivables as c WHERE check_status='1' and TO_DAYS(return_time)=TO_DAYS(NOW()) AND owner_user_id in

    #{id}
) as receivables_money,
(SELECT COUNT(*) FROM wk_crm_activity as c WHERE type = 1 and TO_DAYS(create_time)=TO_DAYS(NOW()) AND create_user_id in

    #{id}
) as activity_num
from wk_crm_contract as a
WHERE TO_DAYS(create_time)=TO_DAYS(NOW()) AND owner_user_id in

    #{id}
 and check_status='1'

SELECT business_id as type_id,2 as type FROM wk_crm_business WHERE TO_DAYS(create_time) = TO_DAYS(NOW()) AND owner_user_id IN

    #{id}
 and status != 3
union all
SELECT contract_id as type_id,3 as type FROM wk_crm_contract WHERE TO_DAYS(create_time) = TO_DAYS(NOW()) AND owner_user_id IN

    #{id}
 and check_status != 7
union all
SELECT customer_id as type_id,1 as type FROM wk_crm_customer WHERE TO_DAYS(create_time) = TO_DAYS(NOW()) and status != 3 AND owner_user_id IN

    #{id}

union all
SELECT receivables_id as type_id,4 as type FROM wk_crm_receivables WHERE check_status = '1' AND TO_DAYS(return_time) = TO_DAYS(NOW()) AND owner_user_id IN

    #{id}

union all
SELECT activity_id as type_id,5 as type FROM wk_crm_activity WHERE type in (1,4) AND TO_DAYS(create_time) = TO_DAYS(NOW()) AND create_user_id IN

    #{id}

choose.when:
select a.customer_id, a.customer_name, a.deal_status, a.create_time, a.owner_user_id,b.realname as owner_user_name,a.last_time from `wk_crm_customer` a left join `wk_admin_user` b on a.owner_user_id = b.user_id where a.customer_id in
#{id}
and a.customer_name like concat('%',#{data.search},'%')

select a.business_id,a.business_name,a.create_time, a.owner_user_id,b.realname as owner_user_name,a.last_time,c.name as statusName from `wk_crm_business` as a left join `wk_admin_user` b on a.owner_user_id = b.user_id left join wk_crm_business_status c on a.status_id = c.status_id where a.status != 3 and a.business_id in
#{id}
and a.business_name like concat('%',#{data.search},'%')

select a.contract_id,a.check_status,a.create_time,a.name,a.last_time, (select group_concat(realname) from `wk_admin_user` where find_in_set(user_id,a.company_user_id)) as company_user_name from `wk_crm_contract` a left join `wk_crm_contacts` b on a.contacts_id = b.contacts_id where a.contract_id in
#{id}
and a.name like concat('%',#{data.search},'%')

select a.receivables_id,a.number,a.return_time, a.owner_user_id,b.realname as owner_user_name from `wk_crm_receivables` a left join `wk_admin_user` b on a.owner_user_id = b.user_id where a.receivables_id in
#{id}
and a.number like concat('%',#{data.search},'%')

select a.activity_id,b.img as userImg,b.realname,a.create_time,a.content,a.category,a.next_time,a.batch_id,a.business_ids,a.contacts_ids,a.activity_type,a.activity_type_id,a.status, (case a.activity_type when 2 then (select customer_name from `wk_crm_customer` where customer_id = a.activity_type_id) when 1 then (select leads_name from `wk_crm_leads` where leads_id = a.activity_type_id) when 5 then (select business_name from `wk_crm_business` where business_id = a.activity_type_id) when 6 then (select name from `wk_crm_contract` where contract_id = a.activity_type_id) when 3 then (select name from `wk_crm_contacts` where contacts_id = a.activity_type_id) end) as crm_type_name from wk_crm_activity as a inner join wk_admin_user as b on a.create_user_id = b.user_id where a.activity_id in
#{id}
and a.activity_type = #{data.crmType}

mapper.select:
SELECT a.activity_type as crmType,COUNT(a.activity_type) as count FROM wk_crm_activity a WHERE a.activity_id in
#{id}
group by a.activity_type

SELECT
a.log_id,a.content,a.tomorrow,a.question,a.create_time,
(CASE WHEN a.category_id = 1 THEN '日报' WHEN a.category_id = 2 THEN '周报' WHEN a.category_id = 3 THEN '月报' END) as category,
(select GROUP_CONCAT(realname) from wk_admin_user where find_in_set(user_id,a.send_user_ids)) as send_user_name,
(select GROUP_CONCAT(name) from wk_admin_dept where find_in_set(dept_id,a.send_dept_ids)) as send_dept_name,
b.dept_id,
b.realname as create_user_name,
soal.customer_ids,
soal.contacts_ids,
soal.business_ids,
soal.contract_ids,
if(soal.customer_ids is null,null,(select GROUP_CONCAT(customer_name SEPARATOR'】、【') from wk_crm_customer where find_in_set(customer_id,soal.customer_ids))) as customer_name,
if(soal.contacts_ids is null,null,(select GROUP_CONCAT(name SEPARATOR'】、【') from wk_crm_contacts where find_in_set(contacts_id,soal.contacts_ids))) as contacts_name,
if(soal.business_ids is null,null,(select GROUP_CONCAT(business_name SEPARATOR'】、【') from wk_crm_business where find_in_set(business_id,soal.business_ids))) as business_name,
if(soal.contract_ids is null,null,(select GROUP_CONCAT(name SEPARATOR'】、【') from wk_crm_contract where find_in_set(contract_id,soal.contract_ids))) as contract_name,
(SELECT COUNT(*) FROM wk_work_task_comment WHERE type_id=a.log_id and type='2') as replyNum
FROM
wk_oa_log as a
LEFT JOIN wk_admin_user as b on a.create_user_id=b.user_id
LEFT JOIN wk_oa_log_relation as soal on soal.log_id=a.log_id
WHERE 1 = 1


        and a.create_user_id in

            #{id}



        and FIND_IN_SET(#{send_user_ids},a.send_user_ids)

            and a.create_user_id in

                #{id}





            and (

                a.create_user_id in

                    #{id}

                or

            FIND_IN_SET(#{send_user_ids},a.send_user_ids)

                and a.create_user_id in

                    #{id}


            )




    and date_format(a.create_time,#{sqlDateFormat}) between #{beginTime} and #{finalTime}


    and category_id = #{categoryId}


    and a.log_id = #{logId}


    and (
    a.content like CONCAT('%',#{search},'%')
    or a.tomorrow like CONCAT('%',#{search},'%')
    or a.question like CONCAT('%',#{search},'%')
    )

order by a.log_id desc
