mapper.select:
select b.task_id,a.label_id,a.name as label_name,a.color
from wk_work_task_label as a left join wk_work_task as b on find_in_set(a.label_id,b.label_id)
where
    b.task_id in

        #{taskId}

SELECT
  COUNT(CASE WHEN a.`status` = '5' THEN 1 ELSE null END) as stopTask,
  COUNT(*) as allTask
FROM
  wk_work_task as a
where ishidden = 0 and a.pid = 0

    and (
    a.main_user_id in

        #{userId}

    or a.create_user_id in

        #{userId}

    or

        FIND_IN_SET(#{userId},a.owner_user_id)

    )


    and a.main_user_id in

        #{userId}



    and a.create_user_id in

        #{userId}



    and

        FIND_IN_SET(#{userId},a.owner_user_id)



    and a.status in(1,2)



        and a.status in(1,2)


        and a.status = #{data.status}



    and a.priority = #{data.priority}


    and date_format(a.stop_time,'%Y-%m-%d') = #{data.dueDate}


    and a.name like concat('%', #{data.search},'%')


    and a.main_user_id = #{data.mainUserId}

select a.*,
(select count(*) from wk_work_task_comment where type_id = a.task_id and type = 1) as commentCount,
(select count(*) from wk_work_task where pid = a.task_id and status = 5) as childWCCount,
(select count(*) from wk_work_task where pid = a.task_id) as childAllCount
from wk_work_task a
where a.pid = 0 and a.ishidden = 0

    and (
    a.main_user_id in

        #{userId}

    or a.create_user_id in

        #{userId}

    or

        FIND_IN_SET(#{userId},a.owner_user_id)

    )


    and a.main_user_id in

        #{userId}



    and a.create_user_id in

        #{userId}



    and

        FIND_IN_SET(#{userId},a.owner_user_id)



    and a.status in(1,2)



        and a.status in(1,2)


        and a.status = #{data.status}



    and a.priority = #{data.priority}


    and date_format(a.stop_time,'%Y-%m-%d') = #{data.dueDate}


    and a.name like concat('%', #{data.search},'%')


    and a.main_user_id = #{data.mainUserId}

order by a.create_time desc

select a.task_id,a.name,a.description,a.start_time,a.stop_time,a.create_time,a.create_user_id,a.main_user_id, (CASE WHEN a.priority = 0 THEN '无' WHEN a.priority = 1 THEN '低' WHEN a.priority = 2 THEN '中' WHEN a.priority = 3 THEN '高' END ) as priority, a.owner_user_id, (select GROUP_CONCAT(name) from wk_work_task_label where find_in_set(label_id,a.label_id) ) as label_name, d.customer_ids, d.contacts_ids, d.business_ids, d.contract_ids from wk_work_task as a left join wk_work_task_relation as d on a.task_id = d.task_id where a.pid = 0 and a.ishidden = 0
and ( a.main_user_id in
#{userId}
or a.create_user_id in
#{userId}
or
FIND_IN_SET(#{userId},a.owner_user_id)
)
and a.main_user_id in
#{userId}
and a.create_user_id in
#{userId}
and
FIND_IN_SET(#{userId},a.owner_user_id)
and a.status in(1,2)
and a.status in(1,2)
and a.status = #{data.status}
and a.priority = #{data.priority}
and date_format(a.stop_time,'%Y-%m-%d') = #{data.dueDate}
and a.name like concat('%', #{data.search},'%')
and a.main_user_id = #{data.mainUserId}
order by a.create_time desc
